    private async Task submitUser()
    {
      displayMessage = false;

      user.id = validateUser.id;
      string etat = await @Service.PostUser(user);
      if (etat == "ok")
      {
        displayMessage = false;
        AppState.selectedBool = true;
        MyUser.user = user;
        _Parent.RefreshState();
        NavManager.NavigateTo("/acceuilchat");
      }
      else
      {
        displayMessage = true;
      }

    }


